import React, { Component } from "react";
// disabled due to hash routing issue:
import { HashRouter as Router, Route } from "react-router-dom";
// import { Router, Route } from "react-router-dom";
import { Provider } from "react-redux";
import Loader from "react-loader-spinner";
import { COLORS } from "./utils/consts";
import store from "./store";
import { loadUser } from "./actions/authActions";
import { getClients } from "./actions/clientsActions";
import Navbar from "./components/general/Navbar.js";
import Landing from "./components/general/Landing.js";
import Clients from "./components/clients/Clients.js";
import Actions from "./components/actions/Actions.js";
import Analytics from "./components/analytics/Analytics";
import SignIn from "./components/auth/SignIn";
import "./styles/app.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      loading: true
    };
  }

  componentDidMount() {
    store.dispatch(getClients());
    // store.dispatch(loadUser());
    // setTimeout(() => this.setState({ loading: false }), 1000);
    // store.dispatch(loadUser());
  }
  // componentDidMount = async ()=> {
  //   const state = await JSON.parse(localStorage.getItem('state'))
  //   if (state) {
  //     this.setState({
  //       movies: state.movies,
  //       users: state.users,
  //       currentUser: state.currentUser,
  //       search: state.search
  //     })
  //   }
  // }

  // async componentDidMount() {
  //   setTimeout(() => {
  //     let data = require('./data.json')
  //     this.setState({
  //       costumers: data
  //     })
  //     console.log("state is updated with data")
  //   }, 1000)
  // }

  render() {
    const { loading } = this.state;

    /*     if (loading) {
      return (
        <div id="general-loader">
          <Loader
            type="Puff"
            color={COLORS["yellow"]}
            height={200}
            width={200}
          />
        </div>
      );
    } */

    return (
      <Provider store={store}>
        <Router>
          <div className="app">
            <div className="general">
              <Navbar />
            </div>
            <div>
              <Route path="/" exact component={Landing} />
              <Route path="/register" exact component={SignIn} />
              <Route path="/clients" exact component={Clients} />
              <Route path="/actions" exact component={Actions} />
              <Route path="/analytics" exact component={Analytics} />
            </div>
          </div>
        </Router>
      </Provider>
    );
  }
}

export default App;
