import axios from "axios";
import { GET_CLIENTS, CLIENTS_LOADING } from "./types";
import { returnErrors } from "./errorActions";
import { URL } from "../utils/consts";

export const getClients = () => (dispatch) => {
  dispatch(setClientsLoading());
  axios
    .get(URL)
    .then((res) => {
      console.log("res from clients backend: ", res.data.data);
      const { data } = res.data;
      console.log("clients in actions ", data);
      dispatch({
        type: GET_CLIENTS,
        payload: data
      });

      /*     this.setState(
        { loading: false, clients: res.data },
        this.updateClientsDisplay
      ); */
    })
    .catch((err) => {
      dispatch(returnErrors(err.response.data, err.response.status));
      console.log("err from clients backend: ", err);
    });
};

export const setClientsLoading = () => {
  return {
    type: CLIENTS_LOADING
  };
};
